
public class ForLoopTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i;
        //for (initiazation;condition;increment)
        for(i=0;i < 3;) {
        	System.out.println(i);
        }
        
        
        int j = 0;
        while(j < 3) {
        	System.out.println(j);
        	j++;
        }
	}

}
